int read_scancode() {
	int scancode = 0;
	EVENT *evp;	
	/* read scancode from event queue */
  	evp = ev_read();
   	if (evp==NULL)
		return 0;
	/* check that it's a Keyboard event */
	if (EV_TAG(*evp) & T_STRING)
		scancode = EV_BUF(*evp)[0];	
	/* remove it from queue */
	ev_pop();	
	return scancode;
}


